import React from 'react';
import {motion} from 'framer-motion';
import Button from '@material-ui/core/Button';
import DeleteIcon from '@material-ui/icons/Delete';

const Modal = ({selected,setSelected})=>{
    const handleIt=(e)=>{
        if(e.target.classList.contains('backdrop')){
            setSelected(null);
        }

    }
    return (<>

    <motion.div className="backdrop"
    initial={{opacity:0}}
    animate={{opacity:1}}
     onClick={handleIt}>

        <motion.img src={selected} initial={{y:"100vh"}} animate={{y:0}} alt="enlarged image"></motion.img>
        <Button variant="contained" color="primary" src={selected} initial={{y:"100vh"}} animate={{y:0}}><DeleteIcon/></Button>
    </motion.div>

   </>);
}
export default Modal;
