import React from 'react';
import useFirestore from '../hooks/useFirestore';
import {motion} from 'framer-motion';
const ImageGrid = ({setSelected})=>{
    const {docs} = useFirestore('images');
    console.log(docs);
    return (<div className="img-grid">
    {docs && docs.map(doc=>(
        <motion.div className="img-wrap" id= {doc.id}
            whileHover={{opacity:1}}
            layout
         onClick={()=>{
            setSelected(doc.url)
        }}>
            <motion.img src = {doc.url} initial={{opacity:0}} animate={{opacity:1}} transition={{delay:1}} alt = "uploaded pic"></motion.img>
        </motion.div>
    ))}
    </div>);
}
export default ImageGrid;
