import React from 'react';
import {motion} from 'framer-motion';
const Modal = ({selected,setSelected})=>{
    const handleIt=(e)=>{
        if(e.target.classList.contains('backdrop')){
            setSelected(null);
        }

    }
    return (<>

    <motion.div className="backdrop"
    initial={{opacity:0}}
    animate={{opacity:1}}
     onClick={handleIt}>

        <motion.img src={selected} initial={{y:"100vh"}} animate={{y:0}} alt="enlarged image"></motion.img>

    </motion.div>
    <motion.div className="backdrop"
    initial={{opacity:0}}
    animate={{opacity:1}}
     onClick={handleIt}>

        <motion.p initial={{y:"100vh"}} animate={{y:-50}} alt="enlarged image">HEY</motion.p>

    </motion.div>
   </>);
}
export default Modal;
