import React from 'react';
const Modal = ({selected,setSelected})=>{
    const handleIt=(e)=>{
        if(e.targer.classList.contains('backdrop')){
            setSelected(null);
        }

    }
    return (<div className="backdrop" onClick={handleIt}>
        <img src={selected} alt="enlarged image"></img>
    </div>);
}
export default Modal;
