import React, { useState,useEffect } from 'react';
import firebase from 'firebase/app';
import Title from './comps/Title';
import UploadForms from './comps/UploadForms';
import ImageGrid from './comps/ImageGrid';
import Modal from './comps/Modal';
import {provider} from './firebase/configure';
import {createGlobalStyle,ThemeProvider} from 'styled-compnents';

const GlobalStyle = createGlobalStyle`
body{
  background-color:${props=>props.theme.mode=='dark'?'#111':'#fff'}
  color:${props=>props.theme.mode==='dark'?'#fff':'#111'}
}`;
function App() {
  const [theme,setTheme] = useState({mode:'light'});
  const [selected,setSelected] = useState(null);
  const [id,getId] = useState(null);
  const [name,setName] = useState(null);
  return (
    <ThemeProvider theme={theme}>
    <GlobalStyle/>
    <div className="App">
      <Title/>
      <UploadForms/>
      <ImageGrid setSelected={setSelected} getId={getId} setName={setName}/>
     {selected && <Modal selected={selected} setSelected={setSelected} id={id} name={name}/>}
    </div>
    </ThemeProvider>
  );
}

export default App;
